import React from "react"
import Link from "next/link"

const CarouselCard = ({ article }) => {
  return (
    <div
      className="w-full h-50vh min-h-l-hero bg-no-repeat bg-cover py-10 flex items-end"
      style={{
        backgroundImage: `url(${article.imageUrl})`
      }}
    >
      <div className="inner-container space-y-3 text-left">
        <h2 className="carousel-category">
          {article.category.name}{" "}
          {/* <span className="carousel-name">&#8226; {article.readTime}</span> */}
        </h2>
        <h1 className="">{article.title}</h1>
        <Link as={`/article/${article.slug}`} href="/article/[id]">
          <a className="px-3 py-2 bg-gl-green text-white inline-block">
            Read Now
          </a>
        </Link>
      </div>
    </div>
  )
}

export default CarouselCard
