import React from "react"
import Link from "next/link"

const CarouselCard = ({ article }) => {
  return (
    <div
      className="w-full h-60vh min-h-l-hero bg-no-repeat bg-cover py-10 flex items-end"
      style={{
        backgroundImage: `url(${article.imageUrl})`
      }}
    >
      <div className="inner-container space-y-2 text-left">
        <h2 className="text-white">
          {article.category.name.toUpperCase()}{" "}
          {/* <span className="carousel-name">&#8226; {article.readTime}</span> */}
        </h2>
        <h1 className="">{article.title}</h1>
        <Link as={`/article/${article.slug}`} href="/article/[id]">
          <a className="px-3 py-2 rounded-sm bg-gl-green text-white inline-block">
            Read Now
          </a>
        </Link>
      </div>
    </div>
  )
}

export default CarouselCard
