import React from "react"
import { Carousel } from "react-responsive-carousel"
import Articles from "../components/articles"
import CarouselCard from "../components/CarouselCard/CarouselCard"
import Layout from "../components/GlobalLayout/layout"
import Seo from "../components/seo"
import { fetchAPI } from "../lib/api"

const Home = ({ articles, categories, homepage }) => {
  console.log(articles)
  const carouselArticles = articles.slice().reverse().slice(0, 5)
  console.log(carouselArticles)
  return (
    <Layout categories={categories}>
      <Seo seo={homepage.seo} />
      <div className="">
        <Carousel
          className="w-full"
          autoPlay="true"
          infiniteLoop={true}
          showArrows={false}
          dynamicHeight={true}
          showStatus={false}
          showThumbs={false}
          className="w-full"
          transitionTime={500}
        >
          {carouselArticles.map((a, i) => (
            <CarouselCard article={a} key={i} />
          ))}
        </Carousel>

        <div className="inner-container mt-24">
          <h1>Latest topics</h1>
          <Articles articles={articles} />
        </div>
      </div>
    </Layout>
  )
}

export async function getStaticProps() {
  // Run API calls in parallel
  const [articles, categories, homepage] = await Promise.all([
    fetchAPI("/articles"),
    fetchAPI("/categories"),
    fetchAPI("/homepage")
  ])

  return {
    props: { articles, categories, homepage },
    revalidate: 1
  }
}

export default Home
